import  { Box, Grid, Paper, Typography } from '@mui/material'
import { BaseGraph } from './BaseGraph';
import { defaultGraphScheme } from '../tools/graphtools';


export const HomeGraph = () => {
    console.log('enter - HomeGraph')
    const [mounted, setMounted] = useState(false)
    // const box1Ref = useRef<HTMLInputElement>(null)
    const box1Ref: React.Ref<any> = useRef()

    useEffect(()=>{
        console.log('homegraph mounted mounted')
        console.log('getWidth', getWidth())
        setMounted(true)
    },[])

    const getWidth = () => {
        console.log('box1Ref', box1Ref)
        let number =  Number(document.getElementById('graph-box1')?.scrollWidth)
            console.log('getWidth',number)
        if ( typeof number === 'number') {
            return number
        } else {
            return 200
        }
    }

    return (<>
        <Box id='heading' display='flex'>
            <Typography align='center' variant='h6' sx={{size:'large'}} color='primary.main'
            >
                Cancer susceptibility gene visualizations using a Graph Database approach
            </Typography>
        </Box>
        <Box display='flex' sx={{flexWrap: 'wrap'}}>
            <Box component='div' ref={box1Ref} display='flex' flexGrow='1' id='graph-box1'>
                <Paper 
                    elevation={4}         
                    sx={{ flexGrow: '1',
                            minWidth: '200px',
                            color: 'white',
                            backgroundColor: 'white',
                            margin: '2px',
                            padding:'5px'}}>
                    <BaseGraph
                        drawerOpen={false}
                        width={getWidth()}
                        height={200}
                        name={'gene'}
                        genes={['BRCA1','BRCA2']}
                        organs={[]}
                        syndromes={[]}
                        diseases={[]}
                        finalVerdict='Confimred'
                        graphScheme={defaultGraphScheme}
                    />
                </Paper>
            </Box>
            <Paper 
                elevation={4}         
                sx={{ flexGrow: '1',
                        minWidth: '200px',
                        color: 'white',
                        backgroundColor: 'white',
                        margin: '2px',
                        padding:'5px'}}>
                {/* <BaseGraph 
                    drawerOpen={false}
                    width={200}
                    height={200}
                    name={'organ'}
                    genes={['BRCA1','BRCA2']}
                    organs={[]}
                    syndromes={[]}
                    diseases={[]}
                    finalVerdict='Confimred'
                    graphScheme={defaultGraphScheme}
                /> */}
            </Paper>
            <Paper 
                elevation={4}         
                sx={{ flexGrow: '1',
                        minWidth: '200px',
                        color: 'white',
                        backgroundColor: 'white',
                        margin: '2px',
                        padding:'5px'}}>
                {/* <BaseGraph 
                    drawerOpen={false}
                    width={200}
                    height={200}
                    name={'syndrome-organ'}
                    genes={['BRCA1','BRCA2']}
                    organs={[]}
                    syndromes={[]}
                    diseases={[]}
                    finalVerdict='Confimred'
                    graphScheme={defaultGraphScheme}
                /> */}
            </Paper>
        </Box>
    </>)
}
