import React, { useState } from 'react'
import  { Box } from '@mui/material'
import { GeneDropdown } from './GeneDropdown'
import { CustomSelect } from './CustomSelect'
import { OrganDropdown } from './OrganDropdown'
import { DiseaseDropdown } from './DiseaseDropdown'

type FiltersProps = {
    name: string
    genes: string[]
    organs: string[]
    diseases: string[]
    onGeneChange: (selcetd: string[]) => void

}

export const Filters = ({
        name, 
        genes,
        organs,
        diseases, 
        onGeneChange,
    } : FiltersProps ) => {
    const [finalVerdict, setFinalVerdict] = useState('Confirmed') 

    const handleGeneChange = (selection: string[]) => {
        onGeneChange(selection)
    }

    const handleOrganChange = (selection: string[]) => {
    }

    const handleDiseaseChange = (selection: string[]) => {
    }

    const handleFinalVerdictChange = (finalVerdict: string) => {
        setFinalVerdict(finalVerdict)

    }

    const DisplayPanel = () => {
        if (name === 'gene' )  {
            return (
                <>
                    <GeneDropdown onChange={handleGeneChange} selected={genes}/>
                    <CustomSelect 
                            options={
                                [
                                    //Do not know how to get the key value yet
                                    {key:'1', value: 'Confirmed'},
                                    {key:'9', value: 'Maybe'}]
                                    // {key:'0', value:  'Unknown'}]
                            }
                            label='Final verdict' 
                            defaultSelected={finalVerdict}
                            onChange={handleFinalVerdictChange}
                    />

                </>
            )     
        } else if (name === 'organ') {
            return (
                <>
                    <OrganDropdown onChange={handleOrganChange} selected={organs}/>
                    <CustomSelect 
                        options={
                            [
                                //Do not know how to get the key value yet
                                {key:'1', value: 'Confirmed'},
                                {key:'9', value: 'Maybe'}]
                                // {key:'0', value:  'Unknown'}]
                        }
                        label='Final verdict' 
                        defaultSelected={finalVerdict}
                        onChange={handleFinalVerdictChange}
                    />
                </>
            )
        } else if (name === 'disease') {
            return (
                <>
                    <DiseaseDropdown onChange={handleDiseaseChange} selected={diseases}/>
                    <CustomSelect 
                        options={
                            [
                                //Do not know how to get the key value yet
                                {key:'1', value: 'Confirmed'},
                                {key:'9', value: 'Maybe'}]
                                // {key:'0', value:  'Unknown'}]
                        }
                        label='Final verdict' 
                        defaultSelected={finalVerdict}
                        onChange={handleFinalVerdictChange}
                    />
                </>
            )
        }
        return ( <></>)
    }


    return (
        <Box 
            id='filter-box'
            display='flex'
            flex={1}
            flexWrap='wrap'
            flexDirection='row-reverse'
        >
            <DisplayPanel />
        </Box>
            )

}
