import React, { useRef } from 'react'
import  { Box, Grid, Paper, Typography } from '@mui/material'
import { BaseGraph } from './BaseGraph';
import { defaultGraphScheme } from '../tools/graphtools';


export const HomeGraph = () => {
    console.log('enter - HomeGraph')

    const getWidth = () => {
        let number =  Number(document.getElementById('graph-box1')?.offsetWidth)
            console.log('getWidth',number)
        if ( typeof number === 'number') {
            return number
        } else {
            return 200
        }
    }

    return (<>
        <Box id='heading' display='flex'>
            <Typography align='center' variant='h6' sx={{size:'large'}} color='primary.main'
            >
                Cancer susceptibility gene visualizations using a Graph Database approach
            </Typography>
        </Box>
        <Box display='flex' sx={{flexWrap: 'wrap'}}>
            <Box display='flex' flexGrow='1' id='graph-box1'>
                <Paper 
                    elevation={4}         
                    sx={{ flexGrow: '1',
                            minWidth: '200px',
                            color: 'white',
                            backgroundColor: 'white',
                            margin: '2px',
                            padding:'5px'}}>
                    <BaseGraph
                        drawerOpen={false}
                        width={getWidth()}
                        height={200}
                        name={'gene'}
                        genes={['BRCA1','BRCA2']}
                        organs={[]}
                        syndromes={[]}
                        diseases={[]}
                        finalVerdict='Confimred'
                        graphScheme={defaultGraphScheme}
                    />
                </Paper>
            </Box>
            <Paper 
                elevation={4}         
                sx={{ flexGrow: '1',
                        minWidth: '200px',
                        color: 'white',
                        backgroundColor: 'white',
                        margin: '2px',
                        padding:'5px'}}>
                <BaseGraph 
                    drawerOpen={false}
                    width={200}
                    height={200}
                    name={'organ'}
                    genes={['BRCA1','BRCA2']}
                    organs={[]}
                    syndromes={[]}
                    diseases={[]}
                    finalVerdict='Confimred'
                    graphScheme={defaultGraphScheme}
                />
            </Paper>
            <Paper 
                elevation={4}         
                sx={{ flexGrow: '1',
                        minWidth: '200px',
                        color: 'white',
                        backgroundColor: 'white',
                        margin: '2px',
                        padding:'5px'}}>
                <BaseGraph 
                    drawerOpen={false}
                    width={200}
                    height={200}
                    name={'syndrome-organ'}
                    genes={['BRCA1','BRCA2']}
                    organs={[]}
                    syndromes={[]}
                    diseases={[]}
                    finalVerdict='Confimred'
                    graphScheme={defaultGraphScheme}
                />
            </Paper>
        </Box>
    </>)
}
