import React, { useRef, useEffect, useInsertionEffect } from 'react'
import { Theme, useTheme } from '@mui/material/styles';
import { useState } from 'react'
import { Box,  Chip, InputLabel, MenuItem, OutlinedInput, FormControl } from '@mui/material'
import Select, { SelectChangeEvent } from '@mui/material/Select';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 4;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 200,
    },
  },
};

function getStyles(value: string, Values: string[], theme: Theme) {
    return {
      fontWeight:
        Values.indexOf(value) === -1
          ? theme.typography.fontWeightRegular
          : theme.typography.fontWeightMedium,
    };
  }

type DropdownProps = {
    label: string
    options: string[]
    onChange?: (selection: string[])=> void
}

export const Dropdown = ({label, options, onChange} : DropdownProps) => {
  console.log('enter = Dropdown')
    const theme = useTheme()
    const [values, setValues] = useState<string[]>([])
    const ref = useRef();

    useEffect(()=>{
      console.log('ref', ref)
    },[])

    const handleChange = (event: SelectChangeEvent<typeof options>) => {
        const {
          target: { value },
        } = event;

        let selection : string[] = []
        if (typeof value === 'string')  {
          selection = value.split(',')}
        else { 
            selection =  value
        }

        setValues(selection)
        if (onChange) onChange(selection) 
      };

    const handelDelete = (e:any, value: string) => {
        console.log('onDelete', value)
        e.preventDefault()
        console.log(values?.filter( (key) => key !==value))
        let selection : string[] = []
        selection =  values?.filter( (key) => key !==value)
        setValues( selection )
        if (onChange) onChange( selection) 
    }

    return (
        <FormControl sx={{m:1, width:'100%'}} size='small'>
            <InputLabel id='multiple-gene-label'>{label}</InputLabel>
            <Select 
                ref={ref}
                sx={{
                  width:'150'
                }}
                labelId='multiple-gene-label'
                id='multiple-gene'
                multiple
                value={values} 
                onChange={handleChange}
                input={<OutlinedInput
                  sx={{width:'150px'}}
                  id='select-multiple-chip' 
                  label='Chip'  
                />}
                renderValue={(selected)=>(
                  <Box sx={{display: 'flex', flexwrap: 'wrap', gap:0.25, border:'1px solid' }}>
                    {selected.map(( value)=> (
                      <Chip 
                        key={value}
                        label={value}
                        onMouseDown={(e)=>{e.stopPropagation()}}
                        onDelete={(e)=>handelDelete(e, value)}
                      />
                    ))}   
                  </Box>
                )}
                MenuProps={MenuProps}
            >
                {options?.map((value)=>(
                    <MenuItem
                        key={value}
                        value={value}
                        style={getStyles(value, values ? values : [], theme)}
                    >
                        {value}
                    </MenuItem>
                ))}
            </Select>
        </FormControl>
    )
}
