import React from 'react'
import { Theme, useTheme } from '@mui/material/styles';
import { useState } from 'react'
import { Box,  Chip, InputLabel, MenuItem, OutlinedInput, FormControl } from '@mui/material'
import Select, { SelectChangeEvent } from '@mui/material/Select';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 4;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 200,
    },
  },
};

const genes = [
    'ABC', 
    'EFG',
    'pop', 
    'XYZ'
]

function getStyles(name: string, geneNames: string[], theme: Theme) {
    return {
      fontWeight:
        geneNames.indexOf(name) === -1
          ? theme.typography.fontWeightRegular
          : theme.typography.fontWeightMedium,
    };
  }

export const Graph = () => {
    const theme = useTheme()

    const [geneNames, setGeneNames] = useState<string[]>([])

    const handleChange = (event: SelectChangeEvent<typeof geneNames>) => {
        const {
          target: { value },
        } = event;
        setGeneNames(
          // On autofill we get a stringified value.
          typeof value === 'string' ? value.split(',') : value,
        );
      };

    const handelDelete = (e:any, value: string) => {
        console.log('onDelete')
        e.preventDefault()
        setGeneNames(genes.filter( (gene) => gene !==value) )
    }

    return (

            <FormControl sx={{m:1, minwidth: 120}} size='small'>
                <InputLabel id='multiple-gene-label'>Gene</InputLabel>
                <Select 
                    labelId='multiple-gene-label'
                    id='multiple-gene'
                    multiple
                    value={geneNames} 
                    onChange={handleChange}
                    input={<OutlinedInput id='select-multiple-chip' label='Chip'  />}
                    renderValue={(selected)=>(
                      <Box sx={{display: 'flex', flexwrap: 'wrap', gap:0.5}}>
                        {selected.map(( value)=> (
                            <Chip 
                              key={value}
                              label={value}
                              clickable={true}
                              onMouseDown={(e)=>{e.stopPropagation()}}
                              onClick={()=>{console.log('click')}}
                              onDelete={(e)=>handelDelete(e, value)}/>
                        ))}   

                      </Box>
                    )}
                    MenuProps={MenuProps}
                >
                    {genes.map((gene)=>(
                        <MenuItem
                        key={gene}
                        value={gene}
                        style={getStyles(gene, geneNames, theme)}

                        >
                          {gene}
                        </MenuItem>
                    ))}
                </Select>
                <Chip 
                              label='value'
                            //   clickable={true}
                              onClick={()=>{console.log('click')}}
                              onDelete={(e)=>handelDelete(e, 'alue')}/>
            </FormControl>

    )


}
