import React, { useState } from 'react'
import  { Box, Divider, Typography } from '@mui/material'
import { CustomSelect } from './CustomSelect'
import { CustomDropdown } from './CustomDropdown'
import { GraphName } from '../tools/graphtools'
import { Filter } from '@mui/icons-material'

const getGraphName = (name: string): GraphName => {
    if (name === 'Disease') { 
        return 'syndrome-disease'
    } else if (name === 'Gene->Disease') {

        return 'syndrome-gene-disease'
    } else {
        return 'syndrome-disease'
    }
}

const getGraphDesc = (name: GraphName) => {
    switch (name) {
    case 'syndrome-disease':
        return 'Disease'
    case 'syndrome-gene-disease':
        return 'Gene->Disease'
    default:
        return 'Disease'
    }
}

type FiltersProps = {
    name: GraphName
    genes: string[]
    organs: string[]
    diseases: string[]
    syndromes: string[]
    finalVerdict: string
    onGraphChange?: (name: GraphName) => void
    onGeneChange?: (selcetd: string[]) => void
    onOrganChange?: (selcetd: string[]) => void
    onDiseaseChange?: (selected: string[]) => void
    onSyndromeChange?: (selected: string[]) => void
    onFinalVerdictChange?: (verdicts: string) => void
}

export const Filters = ({
        name, 
        genes,
        organs,
        diseases, 
        syndromes, 
        finalVerdict,
        onGraphChange,
        onGeneChange,
        onOrganChange,
        onDiseaseChange,
        onSyndromeChange,
        onFinalVerdictChange
} : FiltersProps ) => {

    const handleGraphChange = (name: string) => {
        if (onGraphChange) { 
            onGraphChange(getGraphName(name))
        }
    }

    const handleGeneChange = (selected: string[]) => {
        if ( onGeneChange)
            onGeneChange(selected)
    }

    const handleOrganChange = (selected: string[]) => {
        if (onOrganChange)
            onOrganChange(selected)
    }

    const handleDiseaseChange = (selected: string[]) => {
        if (onDiseaseChange)
          onDiseaseChange(selected)
    }

    const handleSyndromeChange = (selected: string[]) => {
        if (onSyndromeChange)
          onSyndromeChange(selected)
    }

    const handleFinalVerdictChange = (verdict: string) => {
        if (onFinalVerdictChange)
            onFinalVerdictChange(verdict)
    }

    const DisplayPanel = () => {
        let __handleChange:  (data: string[])=>void
        let __selected: string[]
        if (name === 'gene' )  {
            __handleChange = handleGeneChange
            __selected = genes
        } else if (name === 'organ') {
            __handleChange = handleOrganChange
            __selected = organs
        } else if (name === 'disease') {
            __handleChange =  handleDiseaseChange
            __selected = diseases
        } else if ( name in ['syndrome-disease','syndrome-gene-disease']) {
            __handleChange =  handleSyndromeChange
            __selected = syndromes
        } else {
            __handleChange = handleGeneChange
            __selected = genes
        }

        type FilterProps = {
            name: GraphName
        }
        const FilterHeader = ({name}:FilterProps) => {
            switch(name) {
                case 'gene': {
                    return (
                        <div>
                            Select as many <span style={{color: 'blue'}}>Genes</span> as you wish to see
                        </div>
                    )
                }
                case 'organ': {
                    return (
                        <div>
                            Select as many <span style={{color: 'red'}}>Organs</span> as you wish to see
                        </div>
                    )
                }
                case 'disease': {
                    return (
                        <div>
                            Select as many <span style={{color: 'purple'}}>Diseases</span> as you wish to see
                        </div>
                    )
                }
                case 'syndrome-disease': 
                case 'syndrome-gene-disease': {
                    return (
                        <div>
                            Select a syndrome graph and as many <span style={{color: 'yellow' , backgroundColor:'black'}}>Syndromes</span> as you wish to see
                        </div>
                    )
                }
                defualt:{
                    return (
                        <div>
                           Select as many <span style={{color: 'red'}}>{name}</span> as you wish to see
                        </div>
                    )

                }
            }
            return (
                <div>
                    Select as many <span style={{color: 'red'}}>{name}</span> as you wish to see
                </div>
            )
        }

        const FilterContent = ({name} : FilterProps) => {
            switch(name) {
                case 'syndrome-disease': 
                case 'syndrome-gene-disease': {
                    return (<>
                        <CustomSelect 
                            options={[
                                {key:'1', value: getGraphDesc('syndrome-disease')},
                                {key:'2', value: getGraphDesc('syndrome-gene-disease')}
                            ]}
                            label='Syndrome Graph' 
                            defaultSelected={getGraphDesc(name)}
                            onChange={handleGraphChange}
                        />
                        <div>
                            Select a syndrome graph and as many <span style={{color: 'yellow' , backgroundColor:'black'}}>Syndromes</span> as you wish to see
                        </div>
                    </>)
                }
                defualt: {
                    return (<></>)

                }
            }
            return (<></>)
        }

        return (
            <> 
                <Box
                    display='flex'
                    flex={1}
                    flexDirection='column'
                    sx={{
                        color: 'grey',
                    }}
                >
                    <Typography 
                        component='div'

                        sx={{
                            textAlign:'left',
                            marginLeft: 1,
                            color: 'black'
                        }}


                    >
                        <FilterHeader name={name}/>
                    </Typography>
                    <FilterContent name={name}/> 
                    <CustomDropdown 
                        name={name} 
                        selected={__selected}
                        onChange={__handleChange}
                    />
                    <CustomSelect 
                            options={[
                                {key:'1', value: 'Confirmed'},
                                {key:'9', value: 'Maybe'}
                            ]}
                            label='Associations' 
                            defaultSelected={finalVerdict}
                            onChange={handleFinalVerdictChange}
                    />
                </Box>

            </>
        )     
    }


    return (
        <Box 
            id='filter-box'
            display='flex'
            flex={1}
            flexDirection='column'
        >
            <DisplayPanel />
        </Box>
    )

}
