import React from 'react'
import { useReadCypher  } from "use-neo4j";
import { Dropdown } from './Dropdown'

type GeneDropdownProps = {
    onChange?: (options: string[]) => void 
}

export const GeneDropdown = ( {onChange}: GeneDropdownProps) => {
    // const handleChange = (data: DropdownProps) =>  {
    //     console.log('enter - onChange.Dropdown ')
    //     const selectedGenes = (data.value as string[]).map(value => { return {text: value ,value: value} })
    //     onChange(selectedGenes)
    //     console.log('exit - onChnage.Dropdown')        
    // }

    const { loading, error, records} = useReadCypher(
            `MATCH (g:MGene) RETURN DISTINCT g.name as name ORDER BY name`)

    if ( loading ) { 
        console.log('loading genes - GeneSelector')
        return ( <Dropdown label='Loading genes' data={[]}/> )
    }

    if ( error ) {
        console.log(error.message)
        return ( <Dropdown label='Error loading genes' data={[]}/> )
    }

    let genes : string[] = []

     records?.forEach(element => {
        genes.push(element.get('name') as string)
    })

    return (
        <Dropdown label='Genes' data={genes} onChange={()=>{ console.log('onChange')}}/>
    )
}
