import React, { useState, memo } from 'react'
import { styled, useTheme } from '@mui/material/styles'
import  { Box, Stack, Divider, useColorScheme, Paper, Drawer } from '@mui/material'
import { GeneDropdown } from './GeneDropdown';
import { OrganDropdown } from './OrganDropdown';
import { Configuration } from './Configuration';
import { GraphScheme, defaultGraphScheme } from '../tools/graphtools';
import { GeneGraph } from './GeneGrpah';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';

const drawerWidth = 240;

const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })<{
  open?: boolean;
}>(({ theme, open }) => ({
  flexGrow: 1,
  padding: theme.spacing(3),
  transition: theme.transitions.create('margin', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  marginRight: -drawerWidth,
  ...(open && {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginRight: 0,
  }),
}));

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-start',
}));

type GraphProps = {
    open: boolean
    onChange: (open: boolean) => void
}

export const Graph = ( { open , onChange} : GraphProps) => {
    const [genes, setGenes] = useState<string[]>([])
    const [organs, setOrgans] = useState<string[]>([])
    const [graphScheme, setGraphScheme] = useState(defaultGraphScheme)

    const handleDrawerClose = () => {
        onChange(open)
    }

    const handleGeneChange = (selection: string[]) => {
        console.log('handleGeneChange', selection)
        setGenes(selection)
    }

    const handleOrganChange = (selection: string[]) => {
        console.log('handleOrganChange', selection)
        setOrgans(selection)
    }

    const handleConfiguationChange = (graphScheme:GraphScheme) => {
        console.log('handlehandleConfiguationChange', graphScheme)
        setGraphScheme(graphScheme)
    }

    return (
        <>
        <Main open={open}>        
            <DrawerHeader />
            <Stack direction='row-reverse' my={1}>
                <Box sx={{width: '400px'}}>
                <GeneDropdown onChange={handleGeneChange}/>
                <OrganDropdown onChange={handleOrganChange}/>
                <Divider sx={{marginLeft:'8px'}}/>
                <Configuration graphScheme={graphScheme} onChange={handleConfiguationChange} />
                </Box>           

            <Paper elevation={4}         
                    sx={{ 
                        display: 'flex',
                        width:'100%',
                        padding:'2px',  
                        flexGrow: 1,
                        color: 'white',
                        '&:hover': {
                        backgroundColor: 'secondary.light'
                        }
                    }}
                >
                <GeneGraph 
                    verified={true}
                    genes={genes}
                    organs={organs}
                    graphScheme={graphScheme}
                />
            </Paper>
        </Stack>
    </Main>
    <Drawer>
    <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
          },
        }}
        variant="persistent"
        anchor="right"
        open={open}
      >
        <DrawerHeader>
          <IconButton onClick={handleDrawerClose}>
            {/* {theme.direction === 'rtl' ?  : <ChevronRightIcon />} */}
            <ChevronLeftIcon />
          </IconButton>
        </DrawerHeader>
        <Divider />
        <Box 
            sx={{ width: drawerWidth, border:'1px solid', backgroundColor:'grey'}}
            role='presentation'
       //     onClick={toggleDrawer(false)}
      //      onKeyDown={toggleDrawer(false)}
        >
            Drawer Content
        </Box>
      </Drawer>

    </Drawer>


    </>
    )

}
