import React, { useRef, useEffect, useInsertionEffect } from 'react'
import { Theme, useTheme } from '@mui/material/styles';
import { useState } from 'react'
import { Box,  Chip, InputLabel, MenuItem, OutlinedInput, FormControl } from '@mui/material'
import Select, { SelectChangeEvent } from '@mui/material/Select';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 4;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 200,
    },
  },
};

const colors = ['Red', 'Blue', 'Yellow']

function getStyles(value: string, Values: string[], theme: Theme) {
    return {
      fontWeight:
        Values.indexOf(value) === -1
          ? theme.typography.fontWeightRegular
          : theme.typography.fontWeightMedium,
    };
  }

type ColorSelectProps = {
    label: string
    onChange?: (selection: string)=> void
}

export const ColorSelect = ({label, onChange} : ColorSelectProps) => {
    const ref = useRef();

    const [color, setColor] = useState('')

    useEffect(()=>{
      console.log('ref', ref)
    },[])

    const handleChange = (event: SelectChangeEvent) => {
        setColor(event.target.value)
        if (onChange) onChange(event.target.value) 
      };

    return (
        <div>
            <FormControl sx={{m:1, width:'98%'}} size='small'>
                <InputLabel id='select-color-label'>Color</InputLabel>
                <Select 
                    ref={ref}
                    labelId='select-color-label'
                    id='select-color'
                    value={value} 
                    onChange={handleChange}
                    input={<OutlinedInput
                    sx={{width:'inherited'}}
                    id='select-multiple-chip' 
                    label='Chip'  
                    />}
                    MenuProps={MenuProps}
                >
                    {colors?.map((value)=>(
                        <MenuItem
                            key={value}
                            value={value}
                        >
                            {value}
                        </MenuItem>
                    ))}
                </Select>
            </FormControl>
        </div>
    )
}
