import React, { useEffect, useRef, useState } from 'react'
import  { Box, Grid, Paper, Typography } from '@mui/material'
import { BaseGraph } from './BaseGraph';
import { defaultGraphScheme } from '../tools/graphtools';


type Dimension = {
    width: number
    height: number
}

export const HomeGraph = () => {
    console.log('enter - HomeGraph')
    const [dim, setDim] = useState<Dimension>({width:200, height:200})
    const box1Ref = useRef<HTMLDivElement>(null)

    useEffect(()=>{
        console.log('HomeGraph mounted')
        window.addEventListener("resize", handleResize )
        setDim( {width: getWidth(), height: 200})
    },[])

    const handleResize = () => {
        console.log('onResize')
        setDim( ()=> ( 
            {width: getWidth(1), height: 200}))
    }

    const getWidth = (box: number) => {

        let number = Number(document.getElementById(`graph-box{box}`)?.offsetWidth )
        if ( typeof number === 'number' && number === number) {
            console.log('getWidth is a number', number)
            return number -10
        } else {
            console.log('getWidth NaN', number)
            return 200
        }
    }


    return (<>
        <Box id='heading' display='flex'>
            <Typography align='center' variant='h6' sx={{size:'large'}} color='primary.main'
            >
                Cancer susceptibility gene visualizations using a Graph Database approach
            </Typography>
        </Box>
        <Box display='flex' 
             flexWrap='wrap'
        >
            <Box id='graph-box1' display='flex' flex={1}
                sx={{
                    minWidth: '250px',
                    backgroundColor: 'primary.main',
                    color: 'white',
                    paddig: '16px',
                }}
                >

                <Paper 
                    elevation={4}         
                    sx={{
                            color: 'white',
                            backgroundColor: 'white',
                            margin: '2px',
                            padding:'2px'}}>
                    <BaseGraph
                        drawerOpen={false}
                        width={getWidth(1)}
                        height={200}
                        name={'gene'}
                        genes={['BRCA1','BRCA2']}
                        organs={[]}
                        syndromes={[]}
                        diseases={[]}
                        finalVerdict='Confimred'
                        graphScheme={defaultGraphScheme}
                    />
                </Paper>
            </Box>
            <Box id='graphbox2' display='flex' flex={1}
                sx={{
                    minWidth: '200px',
                    backgroundColor: 'primary.main',
                    color: 'white',
                    paddig: '16px',
                }}
                >

                <Paper 
                    elevation={4}         
                    sx={{   
                            color: 'white',
                            width: '100%',
                            backgroundColor: 'white',
                            margin: '2px',
                            padding:'5px'}}>
                    <BaseGraph 
                        drawerOpen={false}
                        width={getWidth(2)}
                        height={200}
                        name={'organ'}
                        genes={['BRCA1','BRCA2']}
                        organs={[]}
                        syndromes={[]}
                        diseases={[]}
                        finalVerdict='Confimred'
                        graphScheme={defaultGraphScheme}
                    />
                </Paper>
            </Box>
            <Box id='graphbox3' display='flex' flex={1}
                sx={{
                    minWidth: '200px',
                    backgroundColor: 'primary.main',
                    color: 'white',
                    paddig: '16px',
                }}
                >

                <Paper 
                    elevation={4}         
                    sx={{ 
                            color: 'white',
                            width: '100%',
                            backgroundColor: 'white',
                            margin: '2px',
                            padding:'5px'}}>
                    <BaseGraph 
                        drawerOpen={false}
                        width={200}
                        height={getWidth(3)}
                        name={'syndrome-organ'}
                        genes={['BRCA1','BRCA2']}
                        organs={[]}
                        syndromes={[]}
                        diseases={[]}
                        finalVerdict='Confimred'
                        graphScheme={defaultGraphScheme}
                    />
                </Paper>
            </Box>
        </Box>
    </>)
}
