import React, { useRef, useEffect, MutableRefObject } from 'react'
import { Theme, useTheme } from '@mui/material/styles';
import { useState } from 'react'
import { Box,  Chip, InputLabel, MenuItem, OutlinedInput, FormControl } from '@mui/material'
import Select, { SelectChangeEvent } from '@mui/material/Select';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 4;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 200,
    },
  },
};

const colors = ['Red', 'Blue', 'Yellow', 'White', 'Black']

function getStyles(value: string, Values: string[], theme: Theme) {
    return {
      fontWeight:
        Values.indexOf(value) === -1
          ? theme.typography.fontWeightRegular
          : theme.typography.fontWeightMedium,
    };
  }

type ColorSelectProps = {
    label: string
    select: string
    inputRef?: MutableRefObject<undefined>
    onChange?: (selection: string)=> void
}

export const ColorSelect = ({label, select,inputRef, onChange} : ColorSelectProps) => {

    const [color, setColor] = useState(select)

    useEffect(()=>{
      console.log('ref', inputRef)
    },[])

    const handleChange = (event: SelectChangeEvent) => {
        console.log(inputRef)
        setColor(event.target.value)
        if (onChange) onChange(event.target.value) 
      };

    return (
        <div>
            <FormControl variant='standard' sx={{m:1, width:'98%'}} size='small'>
                <InputLabel id='select-color-label'>{label}</InputLabel>
                <Select 
                    inputRef={inputRef}
                    labelId='select-color-label'
                    id='select-color'
                    value={select} 
                    onChange={handleChange}
                    label={label}
                >
                    {colors?.map((value)=>(
                        <MenuItem
                            value={value}
                        >
                            {value}
                        </MenuItem>
                    ))}
                </Select>
            </FormControl>
        </div>
    )
}
