                        graphScheme={defaultGraphScheme}
                    /> */}
                </Paper>
            </div>
            <div ref={box1Ref} id='graph-box2' style={{display:'flex', flexGrow:'1' }}>
                <Paper 
                    elevation={4}         
                    sx={{   color: 'white',
                            width: '100%',
                            backgroundColor: 'white',
                            margin: '2px',
                            padding:'5px'}}>
                    {/* <BaseGraph 
                        drawerOpen={false}
                        width={200}
                        height={200}
                        name={'organ'}
