import  { Box, Grid, Paper, Typography } from '@mui/material'
import { BaseGraph } from './BaseGraph';
import { defaultGraphScheme } from '../tools/graphtools';


type Dimension = {
    width: number
    height: number
}

export const HomeGraph = () => {
    console.log('enter - HomeGraph')
    const [mounted, setMounted] = useState(false)
    const [dim, setDim] = useState<Dimension>({width:200, height:200})
    const box1Ref = useRef<HTMLDivElement>(null)
    // const box1Ref: React.Ref<any> = useRef()

    useEffect(()=>{
        console.log('homegraph mounted mounted')
        // console.log('box1Ref', (box1Ref.current as HTMLElement).getBoundingClientRect())
        // console.log('getWidth', getWidth())
        window.addEventListener("resize", handleResize )
        setDim( {width: getWidth(), height: 200})
        // setMounted(true)
    },[])

    const getWidth = () => {
        let number =  Number(document.getElementById('graph-box1')?.offsetWidth )
        console.log('getWidth', number)
        if ( typeof number === 'number') {
            return number -10
        } else {
            return 200
        }
    }

    const handleResize = () => {
        console.log('onResize')
        setDim( ()=> ( 
            {width: getWidth(), height: 200}))
    }

    return (<>
        <Box id='heading' display='flex'>
            <Typography align='center' variant='h6' sx={{size:'large'}} color='primary.main'
            >
                Cancer susceptibility gene visualizations using a Graph Database approach
            </Typography>
        </Box>
        <Box display='flex' sx={{flexWrap: 'wrap'}}>
            <div ref={box1Ref} id='graph-box1' style={{display:'flex', flexGrow:'1' }}>
                <Paper 
                    elevation={4}         
                    sx={{ flexGrow: '1',
                            minWidth: '50px',
                            color: 'white',
                            backgroundColor: 'white',
                            margin: '2px',
                            padding:'5px'}}>
                    <BaseGraph
                        drawerOpen={false}
                        width={getWidth()}
                        height={200}
                        name={'gene'}
                        genes={['BRCA1','BRCA2']}
                        organs={[]}
                        syndromes={[]}
                        diseases={[]}
                        finalVerdict='Confimred'
                        graphScheme={defaultGraphScheme}
                    />
                </Paper>
            </div>
            <Paper 
                elevation={4}         
                sx={{ flexGrow: '1',
                        minWidth: '250px',
                        color: 'white',
                        backgroundColor: 'white',
                        margin: '2px',
                        padding:'5px'}}>
                {/* <BaseGraph 
                    drawerOpen={false}
                    width={200}
                    height={200}
                    name={'organ'}
                    genes={['BRCA1','BRCA2']}
                    organs={[]}
                    syndromes={[]}
                    diseases={[]}
                    finalVerdict='Confimred'
                    graphScheme={defaultGraphScheme}
                /> */}
            </Paper>
            <Paper 
                elevation={4}         
                sx={{ flexGrow: '1',
                        minWidth: '200px',
                        color: 'white',
                        backgroundColor: 'white',
                        margin: '2px',
                        padding:'5px'}}>
                {/* <BaseGraph 
                    drawerOpen={false}
                    width={200}
                    height={200}
                    name={'syndrome-organ'}
                    genes={['BRCA1','BRCA2']}
                    organs={[]}
                    syndromes={[]}
                    diseases={[]}
                    finalVerdict='Confimred'
                    graphScheme={defaultGraphScheme}
                /> */}
            </Paper>
        </Box>
    </>)
}
