import React, { useContext, useEffect, useState } from 'react'
import { Neo4jContext } from 'use-neo4j'
import { loadGene, loadOrgan } from '../tools/graphdata'
import { Dropdown } from './Dropdown'

type CustomDropdownProps = {
    name: 'gene' | 'organ'
    selected: string[]
    onChange?: (selected: string[]) => void 
}

export const CustomDropdown = ( {name, selected, onChange }: CustomDropdownProps) => {

    const context = useContext(Neo4jContext), driver = context.driver

    const [data, setData] = useState<string[]>([])

    useEffect(()=> {
        if (name === 'gene') {
            loadGene(driver, handleData)
        } else if (name === 'organ') {
            loadOrgan(driver, handleData)
        }
        console.log('loading data')
    },[])

    const handleData = (data: string[]) => {
        setData(data)
    }

    const handleChange = (selection: string[] ) =>  {
        if (onChange) {
           onChange(selection)
        }
    }

    const getLabelName = (name: string) => {
        if (name === 'gene')
            return 'Filter Gene'
        else if (name === 'organ') 
            return 'Filter Organ'
        else return 'Filter nor defined'


    }

    return (
        <Dropdown 
            label={getLabelName()}
            options={data} 
            selected={selected}
            onChange={handleChange}
        />
    )
}
