import React, { useRef, useEffect, useInsertionEffect } from 'react'
import { Theme, useTheme } from '@mui/material/styles';
import { useState } from 'react'
import { Box,  Chip, InputLabel, MenuItem, OutlinedInput, FormControl } from '@mui/material'
import Select, { SelectChangeEvent } from '@mui/material/Select';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 4;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 200,
    },
  },
};

function getStyles(value: string, Values: string[], theme: Theme) {
    return {
      fontWeight:
        Values.indexOf(value) === -1
          ? theme.typography.fontWeightRegular
          : theme.typography.fontWeightMedium,
    };
  }

type ColorSelectProps = {
    label: string
    options: string[]
    onChange?: (selection: string)=> void
}

export const ColorSelect = ({label, options, onChange} : ColorSelectProps) => {
    const theme = useTheme()
    const [value, setValues] = useState<string>('')
    const ref = useRef();

    useEffect(()=>{
      console.log('ref', ref)
    },[])

    const handleChange = (event: SelectChangeEvent<string>) => {
        const {
          target: { value },
        } = event;


        setValues(value)
        if (onChange) onChange(value) 
      };

    return (
        <FormControl sx={{m:1, width:'98%'}} size='small'>
            <InputLabel id='multiple-gene-label'>{label}</InputLabel>
            <Select 
                ref={ref}
                labelId='multiple-gene-label'
                id='multiple-gene'
                value={value} 
                onChange={handleChange}
                input={<OutlinedInput
                  sx={{width:'inherited'}}
                   id='select-multiple-chip' 
                   label='Chip'  
                />}
                MenuProps={MenuProps}
            >
                {options?.map((value)=>(
                    <MenuItem
                        key={value}
                        value={value}
                    >
                        {value}
                    </MenuItem>
                ))}
            </Select>
        </FormControl>
    )
}
