import React, { useState } from 'react'
import  { Box } from '@mui/material'
import { GeneDropdown } from './GeneDropdown'
import { CustomSelect } from './CustomSelect'
import { OrganDropdown } from './OrganDropdown'
import { DiseaseDropdown } from './DiseaseDropdown'
import { CustomDropdown } from './CustomDropdown'

type FiltersProps = {
    name: string
    genes: string[]
    organs: string[]
    diseases: string[]
    finalVerdict: string
    onGeneChange?: (selcetd: string[]) => void
    onOrganChange?: (selcetd: string[]) => void
    onDiseaseChange?: (selected: string[]) => void
    onSyndromeChange?: (selected: string[]) => void
    onFinalVerdictChange?: (verdicts: string) => void

}

export const Filters = ({
        name, 
        genes,
        organs,
        diseases, 
        finalVerdict,
        onGeneChange,
        onOrganChange,
        onDiseaseChange,
        onSyndromeChange,
        onFinalVerdictChange
} : FiltersProps ) => {

    const handleGeneChange = (selected: string[]) => {
        if ( onGeneChange)
            onGeneChange(selected)
    }

    const handleOrganChange = (selected: string[]) => {
        if (onOrganChange)
            onOrganChange(selected)
    }

    const handleDiseaseChange = (selected: string[]) => {
        if (onDiseaseChange)
          onDiseaseChange(selected)
    }

    const handleSyndomreChange = (selected: string[]) => {
        if (onSyndromeChange)
          onSyndromeChange(selected)
    }

    const handleFinalVerdictChange = (verdict: string) => {
        if (onFinalVerdictChange)
          onFinalVerdictChange(verdict)

            // setFinalVerdict(finalVerdict)

    }

    const DisplayPanel = () => {
        if (name === 'gene' )  {
            return (
                <>
                    <CustomDropdown 
                        name={name} 
                        selected={genes}
                        onChange={handleGeneChange} />
                    <CustomSelect 
                            options={
                                [
                                    //Do not know how to get the key value yet
                                    {key:'1', value: 'Confirmed'},
                                    {key:'9', value: 'Maybe'}]
                                    // {key:'0', value:  'Unknown'}]
                            }
                            label='Final verdict' 
                            defaultSelected={finalVerdict}
                            onChange={handleFinalVerdictChange}
                    />

                </>
            )     
        } else if (name === 'organ') {
            return (
                <>
                    <CustomDropdown  
                        name={name}
                        selected={organs}
                        onChange={handleOrganChange}
                    />
                    <CustomSelect 
                        options={
                            [
                                //Do not know how to get the key value yet
                                {key:'1', value: 'Confirmed'},
                                {key:'9', value: 'Maybe'}]
                                // {key:'0', value:  'Unknown'}]
                        }
                        label='Final verdict' 
                        defaultSelected={finalVerdict}
                        onChange={handleFinalVerdictChange}
                    />
                </>
            )
        } else if (name === 'disease') {
            return (
                <>
                    <CustomDropdown  
                        name={name}
                        selected={organs}
                        onChange={handleDiseaseChange}
                    />

                    <CustomSelect 
                        options={
                            [
                                //Do not know how to get the key value yet
                                {key:'1', value: 'Confirmed'},
                                {key:'9', value: 'Maybe'}]
                        }
                        label='Final verdict' 
                        defaultSelected={finalVerdict}
                        onChange={handleFinalVerdictChange}
                    />
                </>
            )
        } else if (name === 'syndrome') {
            return (
                <>
                    <DiseaseDropdown onChange={handleDiseaseChange} selected={diseases}/>
                    <CustomSelect 
                        options={
                            [
                                //Do not know how to get the key value yet
                                {key:'1', value: 'Confirmed'},
                                {key:'9', value: 'Maybe'}]
                                // {key:'0', value:  'Unknown'}]
                        }
                        label='Final verdict' 
                        defaultSelected={finalVerdict}
                        onChange={handleFinalVerdictChange}
                    />
                </>
            )
        }
        return ( <></>)
    }


    return (
        <Box 
            id='filter-box'
            display='flex'
            flex={1}
            flexWrap='wrap'
            flexDirection='row-reverse'
        >
            <DisplayPanel />
        </Box>
            )

}
